'use strict';

/*
  The postfix and prefix forms
  importance: 5

  What are the final values of all variables a, b, c and d after the code below?
*/
let a = 1, b = 1;

/* 1. */ let c = ++a; //
/* 2. */ let d = b++; //


/*
  Answer:

  a = 2
  b = 2
  c = 2
  d = 1
*/



/*
  Assignment result
  importance: 3

  What are the values of a and x after the code below?
*/
let a = 2;
let x = 1 + (a *= 2);


/*
  Answer:

  a = 4
  x = 5
*/

